events_tags.map:
function(doc) {
  if (doc.type == 'event') {
      var tags = doc.tags;
      for (var i=0; i<tags.length; i++) {
          emit(tags[i], doc._id);
      }
  }
}

events_tags.reduce:
function(keys, values, rereduce) {
    var eventIDs = [];
    values.forEach(function(v) {
        eventIDs = eventIDs.concat(v);
    });
    return eventIDs;
}

