newView.map:
function(doc) {
    if (doc.type == 'contact') {
        for (var i in doc.groups) {
            emit(i,doc.name);
        }
    } else if (doc.type == 'group') {
        emit(doc._id,doc);
    }
}

attendee_recommended_events.map:
function(doc) {
  if (doc.type == 'attendee') {
    emit([doc._id, 0], doc);
    if (doc.recommendedEvents) {
        for (var i in doc.recommendedEvents) {
            emit([doc._id, Number(i)+1], {_id : doc.recommendedEvents[i]});
        }
    }
  }
}
